generated.css:
#root {
    background-color: white;
    background-image: url(https://s-media-cache-ak0.pinimg.com/originals/0c/b2/4c/0cb24c635425bf162566683b1154c533.jpg);
    margin: none;
    display: grid;
    padding: -40%;
    min-height: 100vh;
    grid-template-rows: min-content min-content auto min-content;
    grid-template-areas:
    "navigation"
    "header"
    "content"
    "footer";
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
}
#navigation {
    background-color: black;
    position: sticky;
    top: 0;
    grid-area: navigation;
    flex-direction: row;
}
#navigation ul {

    text-align: center;
    font-family:'Courier New', Courier, monospace;

}

#navigation li {
    font-weight: 200;
    letter-spacing: 1px;
    background-color: yellowgreen;
    padding: 20px;
}
.container {
    width: 100%;
    padding: -45%; 
}


.dropdown {
    position: relative;
    display: none;
}

#navigation > ul > li {
    display: inline;
    position: relative;
    margin: 10px;
 }

#navigation .dropdown {
    position: absolute;
    display: none;
    top:20px;
    left:0;
 }

#navigation a:hover + .dropdown ,
#navigation a + .dropdown:hover {
    display: block;
 }

#navigation > ul {
    display: flex;
    align-items: center;
    justify-content: space-around;
    flex-direction: row;
    padding: 0;
    flex-direction: column;
}

li {
    font-weight: 300;
    font-family: 'Times New Roman', Times, serif
}

#Summer {
    width: 80%;
    max-width: 900px;
    margin: 0 auto;
    color: lightblue;
    grid-area: header;
}

#Summer h1 {
    text-transform: uppercase;
    color: lightblue;
    text-align: left;
    letter-spacing: 4px;
    background-color: yellow;
    background-size: 800px;
}


#Summer p {
    font-family: 'Times New Roman', Times, serif;
    color: grey;
    font-size: 22px;
    font-weight: bold;
    width: 100%;
    line-height: 2.0em;
    text-align: center;
    border-style: ridge;
    border-color: grey;
    border-width: 3px 3px;
    min-width: 400px;
    max-width: 6900px;
    background-color: whitesmoke;

}

h2 {
    border-style: solid;
    border-width: 45px 240px;
    height: 20%;
    width: 58%;
    border-color:red;
    text-align: center;
    font-family: 'Fontdiner Swanky', cursive;
    font-size: 35px;
    color: yellow;
    background-color: royalblue;

}

#img .img-one {
    border-style: solid;
    position: relative;
}
#content {
    text-align: center;
    margin: 0;
}
#content p {
    width: 100%;
    height: 20%;
    border-style: solid;
    border-color: lightslategray;
    border-width: 3px 3px; 
    text-align: center;
    font-family: 'IM Fell Double Pica SC', serif;
    font-size: 1.5em;    
}
.img-one p {
    border-style: none;
}

#footer {
    width: 100%;
    background-color: forestgreen;
    margin: 0;
    font-family: 'Bangers', cursive;
    grid-area: footer;
    font-size: 25px;
    font-size: 28px;
    text-align: center;
}

.container > h2,
.container > h3 {
}

#Footer h4 {
    text-align: center;

}

@media (min-width: 750px) {
    #root {
        grid-template-columns: 15% auto;
        grid-template-rows: min-content auto min-content;
        grid-template-areas: 
            "navigation header"
            "navigation content"
            "navigation footer";
    }
}


#middle {
    font-size: 1em;
    font-style: italic;
    background-color: forestgreen;
    text-align: center;
}
